const blessed = require('blessed')

const screen = blessed.screen({
  smartCSR: true
})

screen.title = 'fen'

const infobox = blessed.box({
  top: 'center',
  left: 'center',
  width: '50%',
  height: '50%',
  tags: true,
  border: { type: 'line' }
})

infobox.setContent(`
  hi! i'm fen!

  - i live in {green-fg}dÃ¼sseldorf{/green-fg}, germany
  - my gender identity is unknown
  - i do code for a living
  - i work for the node.js foundation
  - i'm pretty good at javascript, erlang and clojure

  come visit me on twitter! {blue-fg}http://twitter.com/feninism{/blue-fg}
  you can also see my code on {blue-fg}http://github.com/fene{/blue-fg}

  bye~
`)

const fenimage = blessed.image({
  height: screen.height,
  file: 'http://fen.party/fen.png',
  type: 'ansi'
})

const guyimage = blessed.image({
  height: screen.height,
  file: 'http://fen.party/big_guy.png',
  ascii: true,
  type: 'ansi',
  right: 0,
  top: 0
})

const guynote = blessed.text({
  left: '60%',
  top: '5%',
  content: 'yea thats guy fieri ->'
})

screen.append(fenimage)
screen.append(guyimage)
screen.append(guynote)
screen.append(infobox)
screen.render()
